package converters;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;import org.springframework.transaction.annotation.Transactional;import domain.Qulp;@Component@Transactionalpublic class QulpToStringConverter implements Converter<Qulp, String> {	@Override	public String convert(final Qulp qulp) {		String res;		if (qulp == null)			res = null;		else			res = String.valueOf(qulp.getId());		return res;	}}