package converters;
import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;import org.springframework.transaction.annotation.Transactional;import org.springframework.util.StringUtils;import repositories.ActivityRepository;import domain.Activity;
@Component @Transactional public class StringToActivityConverter implements Converter<String, Activity>{
	@Autowired 	ActivityRepository activityRepository; 	@Override 	public Activity convert(String textIn) { 		Activity res; 		int id; 		try {			if (StringUtils.isEmpty(textIn)){				res = null;			}else{				id = Integer.valueOf(textIn);				res = this.activityRepository.findOne(id);			}		}catch(final Throwable oops){			throw new IllegalArgumentException(oops);		}		return res;	}}