package converters;
import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;import org.springframework.transaction.annotation.Transactional;import org.springframework.util.StringUtils;import repositories.BaseRepository;import domain.Base;
@Component @Transactional public class StringToBaseConverter implements Converter<String, Base>{
	@Autowired 	BaseRepository baseRepository; 	@Override 	public Base convert(String textIn) { 		Base res; 		int id; 		try {			if (StringUtils.isEmpty(textIn)){				res = null;			}else{				id = Integer.valueOf(textIn);				res = this.baseRepository.findOne(id);			}		}catch(final Throwable oops){			throw new IllegalArgumentException(oops);		}		return res;	}}