package converters;
import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;import org.springframework.transaction.annotation.Transactional;import org.springframework.util.StringUtils;import repositories.ConferenceRepository;import domain.Conference;
@Component @Transactional public class StringToConferenceConverter implements Converter<String, Conference>{
	@Autowired 	ConferenceRepository conferenceRepository; 	@Override 	public Conference convert(String textIn) { 		Conference res; 		int id; 		try {			if (StringUtils.isEmpty(textIn)){				res = null;			}else{				id = Integer.valueOf(textIn);				res = this.conferenceRepository.findOne(id);			}		}catch(final Throwable oops){			throw new IllegalArgumentException(oops);		}		return res;	}}