package converters;
import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;import org.springframework.transaction.annotation.Transactional;import org.springframework.util.StringUtils;import repositories.CreditcardRepository;import domain.CreditCard;
@Component @Transactional public class StringToCreditCardConverter implements Converter<String, CreditCard>{
	@Autowired 	CreditcardRepository creditcardRepository; 	@Override 	public CreditCard convert(String textIn) { 		CreditCard res; 		int id; 		try {			if (StringUtils.isEmpty(textIn)){				res = null;			}else{				id = Integer.valueOf(textIn);				res = this.creditcardRepository.findOne(id);			}		}catch(final Throwable oops){			throw new IllegalArgumentException(oops);		}		return res;	}}