package converters;
import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;import org.springframework.transaction.annotation.Transactional;import org.springframework.util.StringUtils;import repositories.PaperRepository;import domain.Paper;
@Component @Transactional public class StringToPaperConverter implements Converter<String, Paper>{
	@Autowired 	PaperRepository paperRepository; 	@Override 	public Paper convert(String textIn) { 		Paper res; 		int id; 		try {			if (StringUtils.isEmpty(textIn)){				res = null;			}else{				id = Integer.valueOf(textIn);				res = this.paperRepository.findOne(id);			}		}catch(final Throwable oops){			throw new IllegalArgumentException(oops);		}		return res;	}}