package converters;
import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;import org.springframework.transaction.annotation.Transactional;import org.springframework.util.StringUtils;import repositories.RegistrationRepository;import domain.Registration;
@Component @Transactional public class StringToRegistrationConverter implements Converter<String, Registration>{
	@Autowired 	RegistrationRepository registrationRepository; 	@Override 	public Registration convert(String textIn) { 		Registration res; 		int id; 		try {			if (StringUtils.isEmpty(textIn)){				res = null;			}else{				id = Integer.valueOf(textIn);				res = this.registrationRepository.findOne(id);			}		}catch(final Throwable oops){			throw new IllegalArgumentException(oops);		}		return res;	}}