package converters;
import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.convert.converter.Converter;import org.springframework.stereotype.Component;import org.springframework.transaction.annotation.Transactional;import org.springframework.util.StringUtils;import repositories.SponsorRepository;import domain.Sponsor;
@Component @Transactional public class StringToSponsorConverter implements Converter<String, Sponsor>{
	@Autowired 	SponsorRepository sponsorRepository; 	@Override 	public Sponsor convert(String textIn) { 		Sponsor res; 		int id; 		try {			if (StringUtils.isEmpty(textIn)){				res = null;			}else{				id = Integer.valueOf(textIn);				res = this.sponsorRepository.findOne(id);			}		}catch(final Throwable oops){			throw new IllegalArgumentException(oops);		}		return res;	}}