package repositories;import java.util.Collection;import java.util.Date;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import domain.Conference;import domain.Finder;@Repositorypublic interface FinderRepository extends JpaRepository<Finder, Integer> {	@Query("select c from Conference c where c.isDraft = false and  (c.title like %?1% or c.venue like %?1% or c.summary like %?1% or c.acronym like %?1% and c.startDate <=?2 and c.startDate>=?3 and c.fee < ?4)")	Collection<Conference> filter(String keyWord, Date minDate, Date maxDate, Double fee);	@Query("select f from Finder f where f.author.id=?1")	Finder findAuthorByFinder(int id);}