package repositories;import java.util.Collection;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import domain.Porter;@Repositorypublic interface PorterRepository extends JpaRepository<Porter, Integer> {	@Query("select p from Porter p where p.administrator.id = ?1")	Collection<Porter> findPortersByAdministratorId(int id);	@Query("select p from Porter p where p.conference.id = ?1 and p.isDraft = 0")	Collection<Porter> findPortersByConferenceId(int conferenceId);}