package repositories;import java.util.Collection;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import domain.Qulp;@Repositorypublic interface QulpRepository extends JpaRepository<Qulp, Integer> {	@Query("select q from Qulp q where q.administrator.id = ?1")	Collection<Qulp> findQulpsByAdministratorId(int id);	@Query("select q from Qulp q where q.conference.id = ?1 and q.isDraft = 0")	Collection<Qulp> findQulpsByConferenceId(int conferenceId);}