package repositories;import java.util.Collection;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import domain.Report;@Repositorypublic interface ReportRepository extends JpaRepository<Report, Integer> {	@Query("select r from Report r where r.reviewer.id = ?1")	Collection<Report> findReportsByReviewerId(int reviewerId);	@Query("select r from Report r where r.submission.conference.id = ?1")	Collection<Report> findReportsByConferenceId(int conferenceId);		@Query("select r from Report r where r.submission.id = ?1 and r.submission.notified = 'true'")	Collection<Report> findReportsBySubmissionId(int submissionId);	@Query("select r from Report r where r.reviewer.id = ?1 and r.submission.id = ?2")	Report findBySubissionAndReviewerId(int reviewerId, int submissionId);}